import React, { useState, Fragment } from "react";
import { Button, DangerButton } from "./Style";
import { atom } from 'recoil';



// Control params - if game is launched and current speed
// export const controlOptions = atom({
//     key: 'controlOptions',
//     default: {
//         isRunning: false,
//         speed: 5,
//     },
// });
// // List of dots in the game - empty by default
// export const wordState = atom({
//     key: 'wordsState',
//     default: [],
// });
export const Word = (props) => {
    const { color, x, y, size, index, onclick } = props;
    const wordStyle = {
        backgroundColor: color,
        height: `${size}px`,
        width: `${size}px`,
        left: `${props.x}px`,
        top: `${props.y}px`,
    };
    return (
        <Fragment>
            <div
                style={wordStyle}
                onclick={() => onClick(index)}
            >{props.text}</div>
            <div>Hello</div>
        </Fragment>
    );
};


export default Word;
