import React, { Component } from 'react';
 
class WordList extends Component {
  constructor(props) {
    super(props);
 
    this.state = {
      list_words: [],
      list_definition: [],
      value: '',
    };
  }
 
  onClearArray = () => {
      this.setState({ list: [] });
  };

  onAddItem = () => {
    this.setState(state => {
      const list = state.list.concat(state.value);
      return {
        value: '',
      };
    });
  };

  render() {
    return (
      <div>
        <ul>
          {(this.state.list || []).map(item => (
            <li key={item}>{item}</li>
          ))}
        </ul>
        <input type="text" placeholder="Type the words" value={this.state.value} onChange={this.onChangeValue}></input>
        <button type="button" onClick={this.onAddItem} disabled={!this.state.value}>Add To List</button>
      </div>
    );
  }
}
 
export default WordList;
