import React, { useState } from "react";
import ReactStoreIndicator from "react-score-indicator";
import { Button } from "./Style";

export const Score = () => {
  const [clicks, setClicks] = useState(0);
  const updateScore = () => setClicks(clicks + 1);

  return (
    <>
      <ReactStoreIndicator
        value={clicks}
        maxValue={100}
        lineWidth={15}
        lineGap={3}
        fadedOpacity={20}
      />
      <Button buttonColor="green" onclick={updateScore}>
        Click Bait
      </Button>
    </>
  );
};
