export const Word = (props) => {
    const {color, x, y, size} = props;
    const wordStyle = {

        backgroundColor: color,
        height:`${size}px`,
        width: `${size}px`,
        left: `${x}px`,
        top: `${y}px`,
    };
    return (
        <div 
            className="word"
            style={wordStyle}
            // onClick={() => onClick(index)}
        ></div>
    );
};


export default Word;
