// import React, { useState, Fragment } from "react";
import React, { useEffect } from "react";
import Speech from "react-speech";

export const Word = (props) => {
  const { color, x, y, size, word, index, onClick, youTyped } = props;
  // console.log("click --->,", click)
  const wordStyle = {
    backgroundColor: color,
    word: word,
    // height: `${size}px`,
    // width: `${size}px`,
    left: `${props.x}px`,
    top: `${props.y}px`,
  };
  useEffect(() => {
    if (youTyped === word) {
      console.log("score");
      <Toast>
        <Toast.Header>
          <img src="holder.js/20x20?text=%20" className="rounded mr-2" alt="" />
          <strong className="mr-auto">Googly Bot</strong>
        </Toast.Header>
        <Toast.Body>Correct!!</Toast.Body>
      </Toast>;
    }
  }, [youTyped]);
  return (
    <div className="word" style={wordStyle} onClick={() => onClick(index)}>
      {word}
      <Speech text={word} textAsButton={true} displayText="â™ª" />
    </div>
  );
};

export default Word;
