import React from 'react';
import  { Component } from 'react';


export default class WordList extends Component {
    state = {
        word: '',
        data: [{word: '', definition: ''}]
    }

    

    change = e => {
        this.props.onChange({ [e.target.name]: e.target.value });
        this.setState({
            [e.target.nage]: e.target.value
        });
    };

    onSubmit = e => {
        e.preventDefault();
        console.log(this.state);

        let word = this.state.word.value;
        let definition = this.state.definition.value;

        this.setState(prevState => ({
            ...prevState,
            data: [...prevState.data, { word, definition}]
        }))
    }

    render() {
        return (
            <input>
            type="text"
            placeholder="Type the words"
            value={data}
            </input>
        )
    }
}
