import React from 'react';
import  { Component } from 'react';

class PlayGame extends Component {
    constructor(props) {
        super(props);
        this.state = {
            words: [],
            isLoaded: false,
        }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(res => res.json())
            .then(json => {
                this.setState({
                    isLoaded: true,
                    words: json,
                })
            });

        
    }

    render() {
        var { isLoaded, items } = this.state;

        if (!isLoaded) {
